$(function(){let n=`${_apiUrl}currenttime`,t=$("#timeList").find("tbody"),i=$("#timeZone");$(document).ready(function(){i.val("UTC");u();$("#getTime").click(function(){$.get(n,{timeZone:i.val()},function(n){t.prepend(r(n))})})});let u=function(){$.get(`${n}/list`,function(n){let u=[];for(var i=0;i<n.length;i++){const t=n[i];u.push(r(t))}t.html(u.join(""))})},r=function(n){return`<tr>
                    <td>${n.currentTimeQueryId}</td>
                    <td>${n.clientIp}</td>
                    <td>${moment(n.time).format("lll")}</td>
                    <td>${n.timeZone}</td>
                    <td>${moment(n.utcTime).format("lll")}</td>
                </tr>`}});